{
    "name": "my-job-2",
    "queue": "default",
    "priority": 1000,
    "schedulable": true,
    "max_errors": 2,
    "groups": [
        {
            "name": "first group",
            "command": "/opt/lightsched/bin/example-gpu-job",
            "envs": ["GPU_INDEX=0", "CUDA_VERSION=10.2"],
            "resources": {
                "cpu": {
                    "cores": "0.9",
                    "frequency": "2.7GHz",
                    "min_frequency": "2.0GHz"
                },
                "gpu": {
                    "cards": "1",
                    "memory": "4Gi",
                    "cuda": "10.2"
                },
                "memory": "2000Mi"
            },
            "tasks": [
                {
                    "name": "basic cmd",
                    "args": "count=100 size=10kb"
                },
                {
                    "name": "more cmd",
                    "command": "/opt/lightsched/bin/second-command",
                    "args": "times 1000",
                    "envs": ["LD_LIBRARY_PATH=/opt/lightsched/bin"],
                    "resources": {
                        "cpu": {
                            "cores": "2.5",
                            "frequency": "2.0GHz"
                        },
                        "memory": "4000Mi"
                    }
                },
                {
                    "name": "last cmd",
                    "command": "/opt/lightsched/bin/last-command",
                    "args": "times 2000",
                    "envs": ["LD_LIBRARY_PATH=/opt/lightsched/bin"],
                    "resources": {
                        "cpu": {
                            "cores": "3.5",
                            "frequency": "2.0GHz"
                        },
                        "memory": "4000Mi"
                    }
                }
            ]
        },
        {
            "name": "second group",
            "workdir": "/data",
            "envs": ["license=piesat", "DEBUG_MODE=0", "LOG_LEVEL=ERROR"],
            "resources": {
                "cpu": {
                    "cores": "2",
                    "frequency": "3000MHz"
                },
                "memory": "4Gi"
            },
            "tasks": [
                {
                    "name": "Init Task",
                    "command": "/opt/lightsched/bin/test-command",
                    "workdir": "/tmp",
                    "args": "GIN_MODE=release",
                    "resources": {
                        "cpu": {
                            "cores": "2.5",
                            "frequency": "2.0GHz"
                        },
                        "memory": "4000Mi"
                    }
                },
                {
                    "name": "Group2 GPU Task",
                    "command": "/opt/lightsched/bin/second-command",
                    "args": "times 1000",
                    "envs": ["LD_LIBRARY_PATH=/opt/lightsched/bin"],
                    "resources": {
                        "gpu": {
                            "cards": "1",
                            "memory": "8Gi",
                            "cuda": "10.2"
                        },
                        "memory": "1Gi"
                    }
                },
                {
                    "name": "Group2 Task",
                    "command": "/opt/lightsched/bin/third-command",
                    "args": "times 1000",
                    "envs": ["LD_LIBRARY_PATH=/opt/omen/bin"],
                    "resources": {
                        "gpu": {
                            "cards": "1",
                            "memory": "8Gi",
                            "cuda": "10.2"
                        },
                        "memory": "1Gi"
                    }
                }
            ]
        }
    ]
}

